export function getImageSizes(type: 'blog' | 'hero' | 'tool' = 'blog') {
  switch (type) {
    case 'blog':
      return '(max-width: 768px) 100vw, (max-width: 1200px) 50vw, 33vw';
    case 'hero':
      return '100vw';
    case 'tool':
      return '(max-width: 768px) 100vw, (max-width: 1200px) 50vw, 400px';
    default:
      return '100vw';
  }
}

export function getImageQuality(type: 'blog' | 'hero' | 'tool' = 'blog') {
  switch (type) {
    case 'blog':
      return 75;
    case 'hero':
      return 85;
    case 'tool':
      return 80;
    default:
      return 75;
  }
}

// Base64 encoded tiny placeholder image
export const blurDataURL = 'data:image/jpeg;base64,';
